import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
import Box from '@material-ui/core/Box';
import Carousel from './Carousel';

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function SummaryDialog({ nominations, openDialog, handleCloseDialog }) {
    const nominationPosters = nominations.map((nomination) => {
        return nomination.Poster;
    });

    return (
        <Box display="flex" justifyContent="center">
            <Dialog
                style={{ width: "50vw" }}
                open={openDialog}
                onClose={handleCloseDialog}
                TransitionComponent={Transition}
                fullWidth={true}
                maxWidth='xl'
                keepMounted
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">{"Your Nominations"}</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        dis your list yo
                    </DialogContentText>
                    <div style={{ height: "100%" }}>
                        <Carousel summary={nominations} />
                    </div>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog} color="primary">
                        Close
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
}
