        //         <div>Title: {title}</div>
        //         <div>Actors: {actors}</div>
        //         <div>Awards: {awards}</div>
        //         <div>Box Office: {boxOffice}</div>
        //         <div>Country: {country}</div>
        //         <div>Director: {director}</div>
        //         <div>Genre: {genre}</div>
        //         <div>Metascore: {metascore}</div>
        //         <div>Plot: {plot}</div>
        //         <div>Runtime: {runtime}</div>
        //         <div>Writer: {writer}</div>
        //         <div>ReleasedYear: {releasedYear}</div>
        //         <div>IMDb Rating: {imdbRating}</div>
        //         <div>Rotten Tomatoes Rating: {rottenTomatoesRating}</div>
        //         <div>
        //             <Button
        //                 endIcon={<FavoriteIcon />}
        //                 size="small"
        //                 variant="contained"
        //                 color="secondary"
        //                 onClick={() => onNominateMovie(movie)} >Nominate</Button>
        //             <Button
        //                 endIcon={<InfoIcon />}
        //                 size="small"
        //                 variant="contained"
        //                 color="primary"
        //                 onClick={() => { onClick() }}
        //             >Info</Button>
        //         </div>
        //     </div>
        // </div>
    );
};

export default AccordionItem;
