import React, { useEffect } from 'react';
import NominationItem from './NominationItem';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import OscarSearching from '../assets/oscar-searching.png';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import OscarFound from '../assets/oscar-found.png';
import AlertWarning from './AlertWarning';
import Button from '@material-ui/core/Button';
import SummaryDialog from './SummaryDialog';
import Slide from '@material-ui/core/Slide';
import Box from '@material-ui/core/Box';

const useStyles = makeStyles((theme) => ({
    paperPadding: {
        padding: '25px',
    },
    imgOscarSearch: {
        marginTop: '25px',
        marginBottom: '15px',
        width: '100%',
    },
    secondaryHeading: {
        color: theme.palette.text.secondary,
        topMargin: '25px',
    },
}));

const NominationList = ({ nominations, onRemoveNomination }) => {
    const classes = useStyles();
    const [openAlert, setOpenAlert] = React.useState(false);
    const [openDialog, setOpenDialog] = React.useState(false);

    useEffect(() => {
        if (nominations.length === 5) {
            setOpenAlert(true);
        } else {
            setOpenAlert(false);
        }
    }, [nominations]);

    const handleCloseAlert = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };

    const renderedList = nominations.map((nomination) => {
        return (
            <NominationItem key={nomination.imdbID} nomination={nomination} onRemoveNomination={onRemoveNomination} />
        );
    });

    const getAlertContent = () => {
        return (
            <Box>
                <Typography className={classes.secondaryHeading}>You have 5 nominations! </Typography>
                <Button style={{ marginLeft: "10px", textTransform: "none", color: "#e9a97c", borderColor: "#e9a97c" }} variant="outlined" onClick={() => setOpenDialog(true)}>
                    View
                </Button>
            </Box>
        );
    };

    if (nominations.length === 0) {
        return (
            <Slide direction="down" in={true} mountOnEnter unmountOnExit>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">Nominations</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography align="center" className={classes.secondaryHeading}>There are no nominations!</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={OscarSearching} />
                        </Grid>
                        <Grid item xs={12}>
                            <Typography align="center">Help Oscar the Penguin look for 5 nominations!</Typography>
                        </Grid>
                    </Grid>
                </Paper>
            </Slide>
        );
    }

    return (
        <div>
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">Nominations</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography align="center" className={classes.secondaryHeading}>You have {nominations.length} nomination{nominations.length === 1 ? '' : 's'}!</Typography>
                        </Grid>
                        <Grid container item xs={12} justify="center">
                            {nominations.length === 5 ? <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={OscarFound} style={{ width: "60%" }} /> : ''}
                        </Grid>
                        {nominations.length === 5 ?
                            <Grid container item xs={12} justify="center">
                                <Button variant="outlined" style={{ borderColor: "#428bca", color: "#428bca", textTransform: "none" }} onClick={() => setOpenDialog(true)}>View Your Nominations</Button>
                            </Grid> : ''}
                    </Grid>
                </Paper>
                {renderedList}
            </div>
            <div>
                <AlertWarning content={getAlertContent()} handleOpen={openAlert} handleClose={handleCloseAlert} />
                <SummaryDialog nominations={nominations} openDialog={openDialog} handleCloseDialog={() => setOpenDialog(false)} />
            </div>
        </div>
    );
};

export default NominationList;
