import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MovieItem from './MovieItem';
import Collapse from '@material-ui/core/Collapse';
import Alert from '@material-ui/lab/Alert';
import Paper from '@material-ui/core/Paper';
import AlertWarning from './AlertWarning';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        marginTop: '25px',
    }
}));

const MovieList = ({ movies, onNominate, nominations, searchError }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [openAlert, setOpenAlert] = useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;
        return (
            <MovieItem panelIndex={index} movie={movie} onNominate={onNominate} nominations={nominations} expanded={expanded} handleChange={handleChange} />
        );
    });

    if (searchError === "Too many results.") {
        return (
            <div>
                <Collapse in={true} >
                    <Paper>
                        <Alert severity="warning">Please be more precise!</Alert>
                    </Paper>
                </Collapse>
                {renderedList}
            </div>
        );
    } else if (searchError === "Movie not found!") {
        return (
            <div>
                <Collapse in={true} >
                    <Paper>
                        <Alert severity="warning">Try to check your spelling.</Alert>
                    </Paper>
                </Collapse>
                {renderedList}
            </div>
        );
    }

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };



    // const onNominateMovie = (nominatedMovie) => {
    //     // Verifies if the nomination has already been nominated
    //     console.log("Current nominations: ");
    //     console.log(nominations);
    //     if (nominations.includes(nominatedMovie)) {
    //         console.log("Already nominated!");
    //     } else if (nominations.length < 5) {
    //         setIsDisable(true);
    //         console.log("New nomination");
    //         onNominate(nominatedMovie);
    //     } else if (nominations.length === 5) {
    //         console.log("Nomination list full");
    //         setOpenAlert(true);
    //     }
    // };

    const handleAlert = () => {
        setOpenAlert(true);
    };


    return (
        <div className={classes.root}>
            {renderedList}
            <AlertWarning handleOpen={openAlert} handleClose={handleClose} handleAlert={handleAlert} />

        </div>
    );
}

export default MovieList;
