import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MovieItem from './MovieItem';
import Slide from '@material-ui/core/Slide';
import Collapse from '@material-ui/core/Collapse';

import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    }
}));

const MovieList = ({ movies, onNominate, nominations, searchError }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    if (searchError === "Too many results.") {
        return (
            <div>Too many results.</div>
        );
    } else if (searchError === "Movie not found!") {
        return (
            // <div>No movies found.</div>
            <Collapse in={true} >
                <Paper>
                    <div>No movies found.</div>
                </Paper>
            </Collapse>
        );
    }

    const renderErrorMessage = () => {
        return (
            <div>nononoo</div>
        );
    };

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;

        return (
            <MovieItem panelIndex={index} movie={movie} onNominate={onNominate} nominations={nominations} expanded={expanded} handleChange={handleChange} />
        );
    });

    return (
        <div className={classes.root}>
            {() => renderErrorMessage}
            {renderedList}
        </div>
    );
}

export default MovieList;
