import './MovieItem.css';
import React, { useState, useEffect } from 'react';
import NA from '../assets/funny.jpg';
import omdb from '../api/omdb';
import Button from '@material-ui/core/Button';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';

import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import MovieDetails from './MovieDetails'
import NominationAlertDialog from './NominationAlertDialog';

const useStyles = makeStyles((theme) => ({
    secondaryHeading: {
        color: theme.palette.text.secondary
    },
    nominateButtonMargin: {
        margin: '15px 0px',
    },
    button: {
        textTransform: "none",
        borderRadius: "18px",
        padding: "7px 20px",
    },
    infoIcon: {
        color: "orange",
        marginRight: "10px",
    },
}));

const MovieItem = ({ movie, onNominate, nominations, panelIndex, expanded, handleChange, handleAlert }) => {

    const movieId = movie.imdbID;
    const [title, setTitle] = useState('');
    const [runtime, setRuntime] = useState('');
    const [releasedYear, setReleasedYear] = useState('');
    const [country, setCountry] = useState('');
    const [rated, setRated] = useState('');
    const [isDisable, setIsDisable] = useState(false);
    const [openDialog, setOpenDialog] = useState(false);

    const classes = useStyles();

    useEffect(() => {
        const fetchData = async () => {
            const response = await omdb.get(
                '',
                {
                    params: {
                        i: movieId,
                        plot: "full"
                    }
                }
            );

            const results = response.data;
            setTitle(results.Title);
            setRuntime(results.Runtime);
            setReleasedYear(results.Year);
            setCountry(results.Country);
            setRated(results.Rated);
        };
        fetchData();
    });

    // Disable and enable button according to nomination
    useEffect(() => {
        if (nominations.includes(movie)) {
            setIsDisable(true);
        } else {
            setIsDisable(false);
        }
    });

    // Extract more detailed information of the movie when more info is needed
    const onNominateMovie = (nominatedMovie) => {
        // Verifies if the nomination has already been nominated
        console.log("Current nominations: ");
        console.log(nominations);
        if (nominations.includes(nominatedMovie)) {
            console.log("Already nominated!");
        } else if (nominations.length === 4) {
            setOpenDialog(true);
            onNominate(nominatedMovie);
            console.log("Nomination is now full");

        } else if (nominations.length < 5) {
            setIsDisable(true);
            console.log("New nomination");
            onNominate(nominatedMovie);

        } else if (nominations.length === 5) {
            console.log("Nomination list full");
            handleAlert();
        }
    };


    const handleClose = () => {
        return false;
    };

    return (
        <React.Fragment>
            <Accordion expanded={expanded === `panel${panelIndex}`} onChange={handleChange(`panel${panelIndex}`)}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls={`panel${panelIndex}bh-content`}
                    id={`panel${panelIndex}bh-header`}
                >
                    <Grid container direction="row">
                        <Grid item sm="3">
                            <img id="movie-poster" alt={movie.Title} src={movie.Poster === "N/A" ? NA : movie.Poster} />
                        </Grid>

                        <Grid alignItems="flex-start" sm="9">
                            <Box display="flex" flexDirection="column" alignItems="center">
                                <Typography variant="h5">{title}</Typography>
                                <Typography className={classes.secondaryHeading}>{rated === "N/A" ? "" : (`${rated}  |  `)}{runtime}  |  {releasedYear} ({country}) </Typography>

                                <Box
                                    // Prevents the nominate button from opening the detail sheet
                                    onClick={(event) => event.stopPropagation()}
                                    onFocus={(event) => event.stopPropagation()}
                                    flexDirection="column"
                                    display="flex"
                                    alignItems="center"
                                    className={classes.nominateButtonMargin}
                                >
                                    <span style={{ cursor: 'not-allowed' }}>
                                        <Button
                                            className={classes.button}
                                            endIcon={<AddCircleOutlineIcon />}
                                            disabled={isDisable}
                                            size="small"
                                            variant="contained"
                                            color="primary"
                                            onClick={() => onNominateMovie(movie)}>
                                            Nominate
                                    </Button>
                                    </span>
                                </Box>
                            </Box>
                        </Grid>
                    </Grid>
                </AccordionSummary>
                <Divider variant="middle" />
                <AccordionDetails>
                    <MovieDetails movieId={movieId} />
                </AccordionDetails>
            </Accordion>
            <NominationAlertDialog openDialog={openDialog} />
        </React.Fragment>
    );
};

export default MovieItem;
