import React, { useState, useEffect } from 'react';
import Typography from '@material-ui/core/Typography';
import omdb from '../api/omdb';
import youtube from '../api/youtube';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import InfoTwoToneIcon from '@material-ui/icons/InfoTwoTone';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';


const useStyles = makeStyles({
    bold: {
        fontWeight: 600
    },
    detailMargin: {
        margin: "10px 15px",
    },
    trailer: {
        width: "100%",
    },
    awardsContainer: {
        borderColor: "green",
        marginTop: '15px',
        padding: '8px 10px',
        backgroundColor: "salmon",
        width: "100%",
    },
});

const MovieDetails = ({ movieId, expanded }) => {
    const classes = useStyles();

    const [actors, setActors] = useState('');
    const [boxOffice, setBoxOffice] = useState('');
    const [director, setDirector] = useState('');
    const [genres, setGenres] = useState([]);
    const [writer, setWriter] = useState('');
    const [ratings, setRatings] = useState([]);
    const [plot, setPlot] = useState('');
    const [title, setTitle] = useState('');
    const [awards, setAwards] = useState('');
    const [trailerUrl, setTrailerUrl] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            const response = await omdb.get(
                '',
                {
                    params: {
                        i: movieId
                    }
                }
            );

            const results = response.data;

            setGenres(results.Genre);
            setActors(results.Actors);
            setBoxOffice(results.BoxOffice);
            setDirector(results.Director);
            setWriter(results.Writer);
            setRatings(results.Ratings);
            setPlot(results.Plot);
            setTitle(results.Title);
            setAwards(results.Awards);


            console.log("Detail response:");
            console.log(results);
        }
        fetchData();
    }, [movieId]);

    // // YouTube API request for trailers, consumes quota
    // useEffect(() => {
    //     const fetchData = async () => {
    //         const searchTerm = title + " trailer";
    //         console.log(searchTerm);
    //         const response = await youtube.get(
    //             '/search',
    //             {
    //                 params: {
    //                     q: searchTerm
    //                 }
    //             }
    //         );
    //         const results = response.data;

    //         console.log("Sent an API request to YouTube");
    //         console.log(results);
    //         setTrailerUrl(`https://youtube.com/embed/${results.items[0].id.videoId}`);
    //     }
    //     fetchData();
    // }, [expanded, title]);

    const displayRatings = (ratings ? ratings.map((rating) => {
        return (
            <div>
                <Typography className={classes.bold}>
                    {rating.Source}
                </Typography>
                <Typography>
                    {rating.Value}
                </Typography>
            </div>
        );
    }) : null);

    const displayTrailer = (trailerUrl ?
        <iframe title={title + 'Trailer'} className={classes.trailer} src={trailerUrl} />
        : null);

    const customCard = (contentText, content) => {
        return (
            <Card square variant="outlined" style={{ padding: "15px" }}>
                <Typography className={classes.bold}>{contentText}</Typography>
                <Typography>{content}</Typography>
            </Card>
        );
    };

    return (
        <Grid container display="flex">
            <Grid container justify="center" spacing={5} style={{ margin: "0px -5px" }}>
                <Grid item xs={12} sm={6} md={4}>
                    {customCard("Box Office", boxOffice)}
                </Grid>
                <Grid item xs={12} sm={6} md={8}>
                    {customCard("Genres", genres)}
                </Grid>
            </Grid>

            <Grid container className={classes.detailMargin}>
                {awards === 'N/A' ? '' :
                    <Grid item container xs={12} >
                        <Box className={classes.awardsContainer} borderBottom={3} display="flex">
                            <InfoTwoToneIcon className={classes.infoIcon} style={{ marginRight: "7px" }} />
                            <Typography>{awards}</Typography>
                        </Box>
                    </Grid>
                }
            </Grid>

            <Grid container className={classes.detailMargin}>
                <Grid item xs={12} sm={12} md={4}>
                    <Card square variant="outlined" style={{ padding: "15px" }}>
                        <Typography>{displayRatings}</Typography>
                    </Card>
                </Grid>
                <Grid container item xs={12} sm={12} md={8} display="flex">
                    {/* {displayTrailer} */}


                    {/* <div style={{ backgroundColor: "red", overflow: "hidden", position: "relative", height: "0" }}>
                        <iframe style={{ left: "0", top: "0", height: "100%", width: "100%", position: "absolute" }} width="853" height="480" src='https://www.youtube.com/embed/QILiHiTD3uc' frameborder='0' title='shit' allowfullscreen></iframe>
                    </div> */}

                    {/* <img style={{ width: "100%" }} width="50%" src="https://www.gannett-cdn.com/presto/2018/10/12/USAT/a4851738-bde1-421f-b465-69f80e4f0df7-MICHELLE_OBAMA_GEORGE_BUSH_AP.jpg" /> */}
                    {/* <iframe title="test" style={{ backgroundColor: "pink", width: "100%", height: "100%" }} /> */}
                    {/* <iframe title="obabma" style={{ flex: "1 1 auto", border: "0" }} src="https://www.gannett-cdn.com/presto/2018/10/12/USAT/a4851738-bde1-421f-b465-69f80e4f0df7-MICHELLE_OBAMA_GEORGE_BUSH_AP.jpg" /> */}

                    <Card>

                        <CardMedia src="https://www.gannett-cdn.com/presto/2018/10/12/USAT/a4851738-bde1-421f-b465-69f80e4f0df7-MICHELLE_OBAMA_GEORGE_BUSH_AP.jpgs" />

                    </Card>
                </Grid>
            </Grid>

            <Grid item className={classes.detailMargin}>
                {customCard("Plot", plot)}
            </Grid>

            <Grid item xs={12} className={classes.detailMargin}>
                {customCard("Director", director)}
                {customCard("Writer", writer)}
                {customCard("Actors", actors)}
            </Grid>
        </Grid>
    );
};

export default MovieDetails;
