import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MovieItem from './MovieItem';
import Collapse from '@material-ui/core/Collapse';
import Alert from '@material-ui/lab/Alert';
import Paper from '@material-ui/core/Paper';
import AlertWarning from './AlertWarning';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import Pengu from '../assets/oscar_found.png';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        marginTop: '25px',
    }
}));

const MovieList = ({ movies, onNominate, nominations, searchError }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [openAlert, setOpenAlert] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    const handleAlert = () => {
        setOpenAlert(true);
    };

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;
        return (
            <MovieItem panelIndex={index} movie={movie} onNominate={onNominate} nominations={nominations} expanded={expanded} handleChange={handleChange} handleAlert={handleAlert} />
        );
    });

    if (searchError === "Too many results.") {
        return (
            <div>
                <Collapse in={true} >
                    <Paper>
                        <Alert severity="warning">Please be more precise!</Alert>
                    </Paper>
                </Collapse>
                {renderedList}
            </div>
        );
    } else if (searchError === "Movie not found!") {
        return (
            <div>
                <Collapse in={true} >
                    <Paper>
                        <Alert severity="warning">Try to check your spelling.</Alert>
                    </Paper>
                </Collapse>
                {renderedList}
            </div>
        );
    }

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };

    const moviePlaceholder = () => {
        return (
            <Card>
                <Grid container>
                    <Grid item>
                        Opening Message
                    </Grid>
                    <Grid item>
                        <img></img>
                    </Grid>
                    <Grid item>
                        Closing Message
                    </Grid>
                </Grid>
            </Card>
        );
    };

    return (
        <div className={classes.root}>
            <div>
                {/* {moviePlaceholder()} */}
                {renderedList.length === 0 ? moviePlaceholder() : renderedList}
            </div>
            <div>
                <AlertWarning handleOpen={openAlert} handleClose={handleClose} />
            </div>
        </div>
    );
}

export default MovieList;
