import Flicking from '@egjs/react-flicking';
import '../align.css';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const Carousel = ({ nominations }) => {

    const getContent = (nomination) => {
        return (
            <div className="panel">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <img width="55%" alt={nomination.Title + " poster"} src={nomination.Poster} />
                </Box>
            </div>
        );
    };

    return (
        <div className="carousel">
            <Grid container spacing={2}>

                <Grid item>
                    <Paper style={{ width: "25px", height: "25px" }} />
                </Grid>

                <Grid item>
                    <Paper style={{ width: "25px", height: "25px" }} />
                </Grid>

            </Grid>
            <div className="container">
                <Flicking className="flicking" gap={25} circular={true} moveType={{ type: "snap", count: 1 }}>
                    {
                        nominations.map(
                            (nomination) => {
                                return getContent(nomination)
                            }
                        )
                    }
                </Flicking >
            </div >
        </div>

    );

};

export default Carousel;
