import React, { useEffect } from 'react';
import NominationItem from './NominationItem';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import oscarSearching from '../assets/oscar_search.png';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Pengu from '../assets/oscar_found.png';
import AlertWarning from './AlertWarning';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles((theme) => ({
    paperPadding: {
        padding: '25px',
    },
    imgOscarSearch: {
        marginTop: '25px',
        marginBottom: '15px',
        width: '100%',
    },
    secondaryHeading: {
        color: theme.palette.text.secondary,
        topMargin: '25px',
    },
}));


const NominationList = ({ nominations, onRemoveNomination }) => {
    const classes = useStyles();
    const [openAlert, setOpenAlert] = React.useState(false);


    useEffect(() => {
        if (nominations.length === 5) {
            setOpenAlert(true);
        } else {
            setOpenAlert(false);
        }
    }, [nominations]);

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };

    const renderedList = nominations.map((nomination) => {
        return (
            <NominationItem key={nomination.imdbID} nomination={nomination} onRemoveNomination={onRemoveNomination} />
        );
    });

    if (nominations.length === 0) {
        return (
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">There are no nominations!</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={oscarSearching} />
                        </Grid>
                        <Grid item xs={12}>
                            <Typography align="center">Help Oscar the Penguin look for them</Typography>
                        </Grid>
                    </Grid>
                </Paper>
            </div>
        );
    }

    return (
        <div>
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">You have {nominations.length} nomination{nominations.length === 1 ? '' : 's'}!</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            {nominations.length === 5 ? <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={Pengu} width="50%" /> : ''}
                        </Grid>
                        {nominations.length === 5 ?
                            <Grid container item xs={12} justify="center">
                                <Button>View Summary</Button>
                            </Grid> : ''}
                    </Grid>
                </Paper>
                {renderedList}
            </div>
            <div>
                <AlertWarning content={<div>You have 5 nominations!<button>View list</button></div>} handleOpen={openAlert} handleClose={handleClose} />
            </div>
        </div>
    );
};

export default NominationList;
