import React, { useState, useEffect } from 'react';
import Typography from '@material-ui/core/Typography';
import omdb from '../api/omdb';
import youtube from '../api/youtube';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Paper from '@material-ui/core/Paper';
import { Chip } from '@material-ui/core';

import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles({
    bold: {
        fontWeight: 600
    }
});
const MovieDetails = ({ movieId }) => {
    const classes = useStyles();

    const [actors, setActors] = useState('');
    const [boxOffice, setBoxOffice] = useState('');
    const [director, setDirector] = useState('');
    const [genres, setGenres] = useState([]);
    const [writer, setWriter] = useState('');
    const [ratings, setRatings] = useState([]);
    const [plot, setPlot] = useState('');
    const [title, setTitle] = useState('');
    const [trailerUrl, setTrailerUrl] = useState('');


    useEffect(() => {
        const fetchData = async () => {
            const response = await omdb.get(
                '',
                {
                    params: {
                        i: movieId
                    }
                }
            );

            const results = response.data;
            let genreArray = [];
            if (results.Genre) {
                genreArray = results.Genre.split(',');
            }


            setActors(results.Actors);
            setBoxOffice(results.BoxOffice);
            setDirector(results.Director);
            setGenres(genreArray);
            setWriter(results.Writer);
            setRatings(results.Ratings);
            setPlot(results.Plot);
            setTitle(results.Title);


            console.log("Detail response:");
            console.log(results);
        }
        fetchData();
    }, [movieId]);

    const displayRatings = (ratings ? ratings.map((rating) => {
        return (
            <div>
                <Typography className={classes.bold}>
                    {rating.Source}
                </Typography>
                <Typography>
                    {rating.Value}
                </Typography>
            </div>
        );
    }) : null);

    const onClick = async () => {
        const searchTerm = title + " trailer";
        console.log(searchTerm);
        const response = await youtube.get(
            '/search',
            {
                params: {
                    q: searchTerm
                }
            }
        );

        const results = response.data;
        console.log("Youtube");
        console.log(results.items[0].id.videoId);
        setTrailerUrl(`https://youtube.com/embed/${results.items[0].id.videoId}`);

    }

    const displayGenres = (genres ? genres.map((genre) => {
        return (
            <Chip label={genre} />
        );
    }) : null);

    const displayTrailer = (trailerUrl ?
        <iframe src={trailerUrl} />

        : null)

    return (
        <Grid display="flex">
            <Grid container>

                <Paper elevation={1} style={{ padding: "15px" }}>
                    <Typography className={classes.bold}>Box Office</Typography>
                    <Typography>{boxOffice}</Typography>
                </Paper>
                {displayGenres}

            </Grid>
            <Grid container>
                <Paper elevation={1} style={{ padding: "15px" }}>
                    {displayRatings}
                </Paper>
            </Grid>
            <Button onClick={onClick}>Trailer</Button>
            {displayUrl}


            <Paper elevation={1} square style={{ padding: "15px" }}>
                <Typography className={classes.bold}>Director</Typography>
                <Typography>{director}</Typography>
            </Paper>

            <Paper elevation={1} square style={{ padding: "15px" }}>
                <Typography className={classes.bold}>Writer</Typography>
                <Typography>{writer}</Typography>
            </Paper>

            <Paper elevation={1} square style={{ padding: "15px" }}>
                <Typography className={classes.bold}>Actors</Typography>
                <Typography>{actors}</Typography>

            </Paper>

            <Paper elevation={1} style={{ padding: "15px" }}>
                <Typography className={classes.bold}>Plot</Typography>
                <Typography>{plot}</Typography>
            </Paper>
        </Grid>
    );
};

export default MovieDetails;
