import React, { useState, useEffect } from 'react';
import Typography from '@material-ui/core/Typography';
import omdb from '../api/omdb';
import youtube from '../api/youtube';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Paper from '@material-ui/core/Paper';
import { Chip } from '@material-ui/core';
import Card from '@material-ui/core/Card';

import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles({
    bold: {
        fontWeight: 600
    },
    detailMargin: {
        margin: "25px 30px",
    },
    trailer: {
        width: "100%",
        // height: "100%",
    },
});

const MovieDetails = ({ movieId, expanded }) => {
    const classes = useStyles();

    const [actors, setActors] = useState('');
    const [boxOffice, setBoxOffice] = useState('');
    const [director, setDirector] = useState('');
    const [genres, setGenres] = useState([]);
    const [writer, setWriter] = useState('');
    const [ratings, setRatings] = useState([]);
    const [plot, setPlot] = useState('');
    const [title, setTitle] = useState('');
    const [rated, setRated] = useState('');
    const [trailerUrl, setTrailerUrl] = useState('');


    useEffect(() => {
        const fetchData = async () => {
            const response = await omdb.get(
                '',
                {
                    params: {
                        i: movieId
                    }
                }
            );

            const results = response.data;
            let genreArray = [];
            if (results.Genre) {
                genreArray = results.Genre.split(',');
            }

            setActors(results.Actors);
            setBoxOffice(results.BoxOffice);
            setDirector(results.Director);
            setGenres(genreArray);
            setWriter(results.Writer);
            setRatings(results.Ratings);
            setPlot(results.Plot);
            setTitle(results.Title);
            setRated(results.Rated);

            console.log("Detail response:");
            console.log(results);
        }
        fetchData();
    }, [movieId]);

    // // YouTube API request for trailers, consumes quota
    // useEffect(async () => {
    //     const searchTerm = title + " trailer";
    //     console.log(searchTerm);
    //     const response = await youtube.get(
    //         '/search',
    //         {
    //             params: {
    //                 q: searchTerm
    //             }
    //         }
    //     );
    //     const results = response.data;
    //     setTrailerUrl(`https://youtube.com/embed/${results.items[0].id.videoId}`);
    // }, [title]);

    const displayRatings = (ratings ? ratings.map((rating) => {
        return (
            <div>
                <Typography className={classes.bold}>
                    {rating.Source}
                </Typography>
                <Typography>
                    {rating.Value}
                </Typography>
            </div>
        );
    }) : null);

    const displayGenres = (genres ? genres.map((genre) => {
        return (
            <Chip label={genre} />
        );
    }) : null);

    const displayTrailer = (trailerUrl ?
        <iframe className={classes.trailer} src={trailerUrl} />
        : null);

    const customCard = (contentText, content) => {
        return (
            <Card square variant="outlined" style={{ padding: "15px" }}>
                <Typography className={classes.bold}>{contentText}</Typography>
                <Typography>{content}</Typography>
            </Card>
        );
    };

    return (
        <Grid display="flex">
            <Grid container className={classes.detailMargin}>
                <Paper elevation={1} style={{ padding: "15px" }}>
                    <Typography className={classes.bold}>Box Office</Typography>
                    <Typography>{boxOffice}</Typography>
                </Paper>
                <Paper elevation={1} style={{ padding: "15px" }}>
                    <Typography className={classes.bold}>Rated</Typography>
                    <Typography>{rated}</Typography>
                </Paper>
                <Grid item>
                    {displayGenres}
                </Grid>

            </Grid>
            <Grid container className={classes.detailMargin}>
                <Paper elevation={1} style={{ padding: "15px", marginRight: "90px" }}>
                    {displayRatings}
                </Paper>
                {displayTrailer}
            </Grid>

            <div className={classes.detailMargin}>
                {customCard("Plot", plot)}
            </div>

            <div className={classes.detailMargin}>
                {customCard("Director", director)}
                {customCard("Writer", writer)}
                {customCard("Actors", actors)}
            </div>
        </Grid>
    );
};

export default MovieDetails;
