import React, { useEffect, useState } from 'react';
import Flicking from '@egjs/react-flicking';
import '../align.css';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const Carousel = ({ summary }) => {
    const [nono, setNono] = useState([]);

    useEffect(() => {
        if (summary.length === 5) {
            setNono(summary);
        }
    }, [summary]);

    const getContent = (nomination) => {
        return (
            <div className="panel">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <img width="55%" alt={nomination.Title + " poster"} src={nomination.Poster} />
                </Box>
            </div>
        );
    };

    // Get the sprocket holes of the filmstrip
    const getSquareHoles = () => {
        let whiteSquares = [];
        for (let i = 0; i < 17; i++) {
            whiteSquares.push(
                <Grid item>
                    <Paper style={{ width: "25px", height: "25px" }} />
                </Grid>
            )
        }
        return whiteSquares;
    };

    return (
        <Grid container className="carousel" direction="column" justify="space-between" alignItems="center">
            {/* Top perforations of the filmstrip */}
            <Grid container item spacing={2} justify="center" style={{ marginTop: "3px" }}>
                {
                    getSquareHoles().map((square) => {
                        return square
                    })
                }
            </Grid>
            {/* Nomination carousel content */}
            <Grid item>
                <div className="container">
                    <Flicking className="flicking" gap={25} circular={true} moveType={{ type: "snap", count: 1 }}>
                        {
                            nono.length === 5 ?
                                summary.map(
                                    (nomination) => {
                                        return getContent(nomination)
                                    }
                                )
                                : ''
                        }
                    </Flicking >
                </div >
            </Grid>
            {/* Bottom perforations of the filmstrip */}
            <Grid container item spacing={2} justify="center" style={{ marginBottom: "3px" }}>
                {
                    getSquareHoles().map((square) => {
                        return square
                    })
                }
            </Grid>
        </Grid>

    );

};

export default Carousel;
