import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import MovieList from './components/MovieList';
import NominationList from './components/NominationList';
import Button from '@material-ui/core/Button';

import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';

import omdb from './api/omdb';

const App = () => {

    const [movies, setMovies] = useState([]);

    const [nominations, setNominations] = useState([]);

    const [searchError, setSearchError] = useState('');

    const onTermSubmit = async (term) => {
        // HTTP request to OMDB API
        const response = await omdb.get(
            '',
            {
                params: {
                    s: term,
                    type: 'movie'
                }
            }
        );
        console.log("Search response:");
        console.log(response);

        if (response.data.Response === "False") {
            setSearchError(response.data.Error);
        } else {
            setSearchError('');
            setMovies(response.data.Search);
        }
    };

    const onNominate = (movie) => {
        setNominations(oldNominations => [...oldNominations, movie]);
    };

    const onRemoveNomination = (movie) => {
        const newList = nominations.filter((movieItem) => movieItem !== movie);
        setNominations(newList);

    };

    const SimpleDialog = (props) => {
        return (
            <Dialog>
                <DialogTitle>Hello world</DialogTitle>
                <div>Monkey see</div>

            </Dialog>
        );
    }

    return (
        <div>
            <div>Shoppies</div>
            <SearchBar onTermSubmit={onTermSubmit} />
            <NominationList nominations={nominations} onRemoveNomination={onRemoveNomination} />
            <div>XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX</div>
            <MovieList movies={movies} onNominate={onNominate} nominations={nominations} searchError={searchError} />

            <Button></Button>

        </div>
    );
};


export default App;
