import React from 'react';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Slide from '@material-ui/core/Slide';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
    button: {
        textTransform: "none",
    },
    removeButton: {
        textTransform: "none",
        color: "red",
    },
}));

const NominationItem = ({ nomination, onRemoveNomination }) => {
    const classes = useStyles();

    return (
        <Slide direction="up" in={true} mountOnEnter unmountOnExit>
            <Paper square variant="outlined" style={{ padding: "15px" }}>
                <div>{nomination.Title}</div>
                <Button
                    className={classes.button}
                    size="small"
                    variant="contained"
                    color="secondary">
                    Info
                </Button>
                <Button className={classes.removeButton} aria-label="delete" onClick={() => onRemoveNomination(nomination)}>
                    Remove
                </Button>
            </Paper>
        </Slide>
    );
};

export default NominationItem;
