import './AccordionList.css'
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AccordionItem from './AccordionItem';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    heading: {
        flexBasis: '33.33%',
        flexShrink: 0,
    },
    secondaryHeading: {
        fontSize: theme.typography.pxToRem(15),
        color: theme.palette.text.secondary,
    },
}));

const AccordionList = ({ movies, onNominate, nominations, searchError }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    if (searchError === "Too many results.") {
        return (
            <div>Too many results.</div>
        );
    } else if (searchError === "Movie not found!") {
        return (
            <div>No movies found.</div>
        );
    }

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;

        return (

            <AccordionItem panelIndex={index} movie={movie} />

        );
    });

    return (
        <div className={classes.root}>
            {renderedList}
        </div>
    );
}

export default AccordionList;
