import React from 'react';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import Paper from '@material-ui/core/Paper';
import Slide from '@material-ui/core/Slide';

const NominationItem = ({ nomination, onRemoveNomination }) => {
    return (
        <Slide direction="up" in="true" mountOnEnter unmountOnExit>
            <Paper square variant="outlined" style={{ padding: "15px" }}>
                <div>{nomination.Title}</div>
                <Button
                    size="small"
                    variant="contained"
                    color="secondary">Info</Button>

                <Button aria-label="delete" onClick={() => onRemoveNomination(nomination)}>
                </Button>
            </Paper>
        </Slide>
    );
};

export default NominationItem;
