import './MovieItem.css';
import React, { useState, useEffect } from 'react';
import NA from '../assets/funny.jpg';
import omdb from '../api/omdb';
import Button from '@material-ui/core/Button';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';

import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import MovieDetails from './MovieDetails'
import InfoTwoToneIcon from '@material-ui/icons/InfoTwoTone';

import Alert from '@material-ui/lab/Alert';
import AlertWarning from './AlertWarning';


const useStyles = makeStyles((theme) => ({
    secondaryHeading: {
        color: theme.palette.text.secondary
    },
    nominateButtonMargin: {
        margin: '15px 0px',
    },
    button: {
        textTransform: "none",
        borderRadius: "18px",
        padding: "7px 20px",
    },
    awardsContainer: {
        borderLeft: "2",
        borderColor: "secondary.main",
        marginTop: '15px',
        padding: '8px 10px',
        backgroundColor: "salmon",
    },
    infoIcon: {
        color: "orange",
        marginRight: "10px",
    },
}));

const MovieItem = ({ movie, onNominate, nominations, panelIndex, expanded, handleChange }) => {

    const movieId = movie.imdbID;
    const [title, setTitle] = useState('');
    const [awards, setAwards] = useState('');
    const [runtime, setRuntime] = useState('');
    const [releasedYear, setReleasedYear] = useState('');
    const [country, setCountry] = useState('');
    const [isDisable, setIsDisable] = useState(false);
    const [openAlert, setOpenAlert] = useState(false);

    const classes = useStyles();

    useEffect(() => {
        const fetchData = async () => {
            const response = await omdb.get(
                '',
                {
                    params: {
                        i: movieId,
                        plot: "full"
                    }
                }
            );

            const results = response.data;
            setTitle(results.Title);
            setAwards(results.Awards);
            setRuntime(results.Runtime);
            setReleasedYear(results.Year);
            setCountry(results.Country);
        };
        fetchData();
    });

    // Disable and enable button according to nomination
    useEffect(() => {
        if (nominations.includes(movie)) {
            setIsDisable(true);
        } else {
            setIsDisable(false);
        }
    });

    // Extract more detailed information of the movie when more info is needed
    const onNominateMovie = (nominatedMovie) => {
        // Verifies if the nomination has already been nominated
        console.log("Current nominations: ");
        console.log(nominations);
        if (nominations.includes(nominatedMovie)) {
            console.log("Already nominated!");
        } else if (nominations.length < 5) {
            setIsDisable(true);
            console.log("New nomination");
            onNominate(nominatedMovie);
        } else if (nominations.length === 5) {
            console.log("Nomination list full");
            setOpenAlert(true);
        }
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };


    return (
        <div>
            <Accordion expanded={expanded === `panel${panelIndex}`} onChange={handleChange(`panel${panelIndex}`)}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls={`panel${panelIndex}bh-content`}
                    id={`panel${panelIndex}bh-header`}
                >
                    <Grid container direction="row">
                        <Grid item sm="3">
                            <img id="movie-poster" alt={movie.Title} src={movie.Poster === "N/A" ? NA : movie.Poster} />
                        </Grid>

                        <Grid alignItems="flex-start" sm="9">
                            <Box display="flex" flexDirection="column" alignItems="center">
                                <Typography variant="h5">{title}</Typography>
                                <Typography className={classes.secondaryHeading}>{releasedYear} ({country})  |  {runtime} </Typography>
                                {/* {awards === 'N/A' ? '' : <Alert severity="info" className={classes.awardsMargin} ><Typography>{awards}</Typography></Alert>} */}
                                {awards === 'N/A' ? '' :
                                    <Box className={classes.awardsContainer} display="flex" >
                                        <InfoTwoToneIcon className={classes.infoIcon} />
                                        <Typography>{awards}</Typography>
                                    </Box>
                                }
                                <Box
                                    // Prevents the nominate button from opening the detail sheet
                                    onClick={(event) => event.stopPropagation()}
                                    onFocus={(event) => event.stopPropagation()}
                                    flexDirection="column"
                                    display="flex"
                                    alignItems="center"
                                    className={classes.nominateButtonMargin}
                                >
                                    <span style={{ cursor: 'not-allowed' }}>
                                        <Button
                                            className={classes.button}
                                            endIcon={<AddCircleOutlineIcon />}
                                            disabled={isDisable}
                                            size="small"
                                            variant="contained"
                                            color="primary"
                                            onClick={() => onNominateMovie(movie)}>
                                            Nominate
                                        </Button>
                                    </span>
                                </Box>
                            </Box>
                        </Grid>
                    </Grid>
                </AccordionSummary>
                <Divider variant="middle" />
                <AccordionDetails>
                    <MovieDetails movieId={movieId} />
                </AccordionDetails>
            </Accordion>
            <AlertWarning handleOpen={openAlert} handleClose={handleClose} />

        </div>
    );
};

export default MovieItem;
