import React, { useEffect } from 'react';
import SearchIcon from '@material-ui/icons/Search';
import TextField from '@material-ui/core/TextField';
import Autocomplete, { createFilterOptions } from '@material-ui/lab/Autocomplete';
import Grid from '@material-ui/core/Grid';
import { FilmData } from './FilmData';
import Card from '@material-ui/core/Card';
import { makeStyles } from '@material-ui/core/styles';
import ErrorTwoTone from '@material-ui/icons/ErrorTwoTone';
import Box from '@material-ui/core/Box';

const useStyles = makeStyles((theme) => ({
    root: {
        padding: '2px 4px',
        display: 'flex',
        alignItems: 'center',
        width: 400,
    },
    input: {
        marginLeft: theme.spacing(1),
        flex: 1,
    },
    searchBar: {
        paddingTop: 11,
        paddingBottom: 13,
        paddingRight: 20,
    },
    errorIcon: {
        marginRight: 5,
    }
}));

const filter = createFilterOptions();

const SearchBar = ({ onTermSubmit, searchError, resultsAmount }) => {
    const classes = useStyles();

    const [value, setValue] = React.useState(null);
    const [term, setTerm] = React.useState('');
    const [errorMessage, setErrorMessage] = React.useState(false);

    useEffect(() => {
        if (searchError !== '') {
            setErrorMessage(searchError === "Incorrect IMDb ID." ? "Please enter a movie title" : searchError);
        } else {
            setErrorMessage(false);
        }

    }, [searchError]);

    const onFormSubmit = (event) => {
        event.preventDefault();
        if (value === null) {
            onTermSubmit(null);
        } else {
            onTermSubmit(value.title);
            setTerm(value.title);
        }
    };

    const errorDisplay = () => {
        return (
            <Box display="flex" alignItems="center">
                <ErrorTwoTone className={classes.errorIcon} />
                {errorMessage}
            </Box>
        );
    };

    return (
        <React.Fragment>
            <Card className={classes.searchBar}>
                <Autocomplete
                    value={value}
                    onChange={(event, newValue) => {
                        if (typeof newValue === 'string') {
                            setValue({
                                title: newValue,
                            });
                        } else if (newValue && newValue.inputValue) {
                            // Create a new value from the user input
                            setValue({
                                title: newValue.inputValue,
                            });
                        } else {
                            setValue(newValue);
                        }
                    }}
                    filterOptions={(options, params) => {
                        const filtered = filter(options, params);

                        // Suggest the creation of a new value
                        if (params.inputValue !== '') {
                            filtered.push({
                                inputValue: params.inputValue,
                                title: `Add "${params.inputValue}"`,
                            });
                        }
                        return filtered;
                    }}
                    selectOnFocus
                    clearOnBlur
                    handleHomeEndKeys
                    id="free-solo-with-text-demo"
                    options={FilmData}
                    getOptionLabel={(option) => {
                        // Value selected with enter, right from the input
                        if (typeof option === 'string') {
                            return option;
                        }
                        // Add "xxx" option created dynamically
                        if (option.inputValue) {
                            return option.inputValue;
                        }
                        // Regular option
                        return option.title;
                    }}
                    renderOption={(option) => option.title}
                    style={{ width: "100%" }}
                    freeSolo
                    renderInput={(params) => (
                        <Grid container direction="row" alignItems="center">
                            <Grid container item xs={2} sm={1} md={1} justify="center">
                                <SearchIcon justify="center" className={classes.searchIcon} />
                            </Grid>
                            <Grid item xs={10} sm={11} md={11}>
                                <form onSubmit={onFormSubmit}>
                                    <TextField
                                        error={errorMessage ? true : false}
                                        helperText={errorMessage ? errorDisplay() :
                                            (resultsAmount === 0 ? '' : `Found ${resultsAmount} results for "${term}"`)
                                        }
                                        size="small"
                                        variant="outlined"
                                        label="Search a Movie"
                                        color="#428bca"
                                        margin="dense"
                                        {...params}
                                    />
                                </form>
                            </Grid>
                        </Grid>
                    )}
                />
            </Card>
        </React.Fragment>
    );
}

export default SearchBar;
