import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Pengu from '../assets/oscar_found.png';
import Slide from '@material-ui/core/Slide';
import NominationList from './NominationList';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import TouchCarousel from 'react-touch-carousel';

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function NominationAlertDialog({ nominations, openDialog, handleCloseDialog }) {
    const nominationPosters = nominations.map((nomination) => {
        return nomination.Poster;
    });

    function CarouselContainer(props) {

    }

    function renderCard(index, modIndex, cursor) {
        const item = nominationPosters[modIndex];

        return (
            <div
                key={index}
                className='carousel-card'
                onClick={() => log(`clicked card ${1 + modIndex}`)}
            >
                <div
                    className='carousel-card-inner'
                    style={{ backgroundColor: item.background }}
                >
                    <div className='carousel-title'>{item.title}</div>
                    <div className='carousel-text'>{item.text}</div>
                </div>
            </div>
        );
    }




    return (

        <div>
            <Dialog
                open={openDialog}
                onClose={handleCloseDialog}
                TransitionComponent={Transition}
                fullWidth={true}
                maxWidth='xl'
                keepMounted
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">{"Your Nominations"}</DialogTitle>
                <DialogContent style={{}}>
                    <DialogContentText id="alert-dialog-description">
                        dis your list yo
                        {/* <img alt="Oscar the Penguin Nomination max" src={Pengu} /> */}
                    </DialogContentText>

                    <div style={{ height: "100%", backgroundColor: "#FFB7B2" }}>
                        {/* {chosenNominations} */}
                        nominations
                        <div>
                            <img src={nominations[0].Poster} />
                        </div>
                    </div>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog} color="primary">
                        Close
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
