import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MovieItem from './MovieItem';
import AlertWarning from './AlertWarning';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import Pengu from '../assets/oscar_found.png';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        marginTop: '25px',
    }
}));

const MovieList = ({ movies, onNominate, nominations, searchError }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [openAlert, setOpenAlert] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    const handleAlert = () => {
        setOpenAlert(true);
    };

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;
        return (
            <MovieItem panelIndex={index} movie={movie} onNominate={onNominate} nominations={nominations} expanded={expanded} handleChange={handleChange} handleAlert={handleAlert} />
        );
    });

    const handleClose = () => {
        setOpenAlert(false);
    };

    const moviePlaceholder = () => {
        return (
            <Card>
                <Grid container direction="column" alignItems="center">
                    <Grid item>
                        Opening Message
                    </Grid>
                    <Grid item>
                        <img alt="Empty theater seats" src={Pengu}></img>
                    </Grid>
                    <Grid item>
                        Closing Message
                    </Grid>
                </Grid>
            </Card>
        );
    };

    return (
        <div className={classes.root}>
            <div>
                {renderedList.length === 0 ? moviePlaceholder() : renderedList}
            </div>
            <div>
                <AlertWarning content="Your nomination list is full!" handleOpen={openAlert} handleClose={handleClose} />
            </div>
        </div>
    );
}

export default MovieList;
