import './MovieItem.css';
import React, { useState } from 'react';
import NA from '../assets/funny.jpg';
import omdb from '../api/omdb';
import Button from '@material-ui/core/Button';
import InfoIcon from '@material-ui/icons/Info';
import FavoriteIcon from '@material-ui/icons/Favorite';

import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    heading: {
        flexBasis: '33.33%',
        flexShrink: 0,
    },
    secondaryHeading: {
        fontSize: theme.typography.pxToRem(15),
        color: theme.palette.text.secondary,
    },
}));

const AccordionItem = ({ movie, onNominate, nominations, panelIndex }) => {
    const movieId = movie.imdbID;

    const [title, setTitle] = useState('');
    const [actors, setActors] = useState('');
    const [awards, setAwards] = useState('');
    const [boxOffice, setBoxOffice] = useState('');
    const [country, setCountry] = useState('');
    const [director, setDirector] = useState('');
    const [genre, setGenre] = useState('');
    const [metascore, setMetascore] = useState('');
    const [plot, setPlot] = useState('');
    const [runtime, setRuntime] = useState('');
    const [writer, setWriter] = useState('');
    const [releasedYear, setReleasedYear] = useState('');
    const [imdbRating, setImdbRating] = useState('');
    const [rottenTomatoesRating, setRottenTomatoesRating] = useState('');

    // from accordionlist
    const classes = useStyles();
    // const [expanded, setExpanded] = React.useState(false);

    // const handleChange = (panel) => (event, isExpanded) => {
    //     setExpanded(isExpanded ? panel : false);
    // };

    // Extract more detailed information of the movie
    const onClick = async () => {
        const response = await omdb.get(
            '',
            {
                params: {
                    i: movieId
                }
            }
        );

        setTitle(response.data.Title);
        setActors(response.data.Actors);
        setAwards(response.data.Awards);
        setBoxOffice(response.data.BoxOffice);
        setCountry(response.data.Country);
        setDirector(response.data.Director);
        setGenre(response.data.Genre);
        setMetascore(response.data.Metascore);
        setPlot(response.data.Plot);
        setRuntime(response.data.Runtime);
        setWriter(response.data.Writer);
        setReleasedYear(response.data.Released);
        // setImdbRating(response.data.Ratings[0].Value);
        // setRottenTomatoesRating(response.data.Ratings[1].Value);

        console.log("Detail response:");
        console.log(response);
    };

    const onNominateMovie = (nominatedMovie) => {
        // Verifies if the nomination has already been nominated
        console.log("Current nominations: ");
        console.log(nominations);
        if (nominations.includes(nominatedMovie)) {
            console.log("Already nominated!");
        } else if (nominations.length < 5) {
            console.log("New nomination");
            onNominate(nominatedMovie);
        }
    };

    return (
        <Accordion expanded={expanded === `panel${panelIndex}`} onChange={handleChange(`panel${panelIndex}`)}>
            <AccordionSummary
                expandIcon={<ExpandMoreIcon />}
                aria-controls={`panel${panelIndex}bh-content`}
                id={`panel${panelIndex}bh-header`}
            >
                <img className={classes.heading} alt={movie.Title} src={movie.Poster === "N/A" ? NA : movie.Poster} />
                <Typography className={classes.secondaryHeading}>{movie.Title}</Typography>

                <div>
                    <Button
                        endIcon={<FavoriteIcon />}
                        size="small"
                        variant="contained"
                        color="secondary"
                        onClick={() => onNominateMovie(movie)} >Nominate</Button>
                    <Button
                        endIcon={<InfoIcon />}
                        size="small"
                        variant="contained"
                        color="primary"
                        onClick={() => { onClick() }}
                    >Info</Button>
                </div>

            </AccordionSummary>
            <AccordionDetails>
                <div>
                    <Typography>Title: {title}</Typography>
                    <Typography>Actors: {actors}</Typography>
                    <Typography>Awards: {awards}</Typography>
                    <Typography>Box Office: {boxOffice}</Typography>
                    <Typography>Country: {country}</Typography>
                    <Typography>Director: {director}</Typography>
                    <Typography>Genre: {genre}</Typography>
                    <Typography>Metascore: {metascore}</Typography>
                    <Typography>Plot: {plot}</Typography>
                    <Typography>Runtime: {runtime}</Typography>
                    <Typography>Writer: {writer}</Typography>
                    <Typography>ReleasedYear: {releasedYear}</Typography>
                    <Typography>IMDb Rating: {imdbRating}</Typography>
                    <Typography>Rotten Tomatoes Rating: {rottenTomatoesRating}</Typography>
                </div>
            </AccordionDetails>
        </Accordion>
        // <div className="movie-item" key={movie.imdbID}>
        //     <img alt={movie.Title} src={movie.Poster === "N/A" ? NA : movie.Poster} />

        //     {console.log(movie)}
        //     <div>
        //         <div>Title: {title}</div>
        //         <div>Actors: {actors}</div>
        //         <div>Awards: {awards}</div>
        //         <div>Box Office: {boxOffice}</div>
        //         <div>Country: {country}</div>
        //         <div>Director: {director}</div>
        //         <div>Genre: {genre}</div>
        //         <div>Metascore: {metascore}</div>
        //         <div>Plot: {plot}</div>
        //         <div>Runtime: {runtime}</div>
        //         <div>Writer: {writer}</div>
        //         <div>ReleasedYear: {releasedYear}</div>
        //         <div>IMDb Rating: {imdbRating}</div>
        //         <div>Rotten Tomatoes Rating: {rottenTomatoesRating}</div>
        // <div>
        //     <Button
        //         endIcon={<FavoriteIcon />}
        //         size="small"
        //         variant="contained"
        //         color="secondary"
        //         onClick={() => onNominateMovie(movie)} >Nominate</Button>
        //     <Button
        //         endIcon={<InfoIcon />}
        //         size="small"
        //         variant="contained"
        //         color="primary"
        //         onClick={() => { onClick() }}
        //     >Info</Button>
        // </div>
        //     </div>
        // </div>
    );
};

export default AccordionItem;
