import './MovieItem.css';
import React from 'react';
import axios from 'axios';

const MovieItem = ({ movie, onNominate, nominations }) => {
    const movieId = movie.imdbID;

    const onClick = async () => {
        const response = await axios.get(
            'http://www.omdbapi.com/?apikey=97e2a442&',
            {
                params: {
                    i: movieId
                }
            }
        );

        console.log("Detail response:");
        console.log(response);
    };


    return (
        <div className="movie-item" key={movie.imdbID}>
            <img onClick={() => { onClick() }} src={movie.Poster} />

            <div>
                <div>Title: {movie.Title}</div>

                <button>Info</button>
                <button onClick={() => { onNominate(movie) }} >Nominate</button>

            </div>
        </div>
    );
};

export default MovieItem;
