import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MovieItem from './MovieItem';
import AlertWarning from './AlertWarning';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import EmptyTheater from '../assets/empty-theater.png';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        marginTop: '25px',
    }
}));

const MovieList = ({ movies, onNominate, nominations, handleLoadMore, resultsAmount }) => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [openAlert, setOpenAlert] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    const handleAlert = () => {
        setOpenAlert(true);
    };

    const renderedList = movies.map((movie) => {
        let index = movies.indexOf(movie) + 1;
        return (
            <MovieItem panelIndex={index} movie={movie} onNominate={onNominate} nominations={nominations} expanded={expanded} handleChange={handleChange} handleAlert={handleAlert} />
        );
    });

    const handleClose = () => {
        setOpenAlert(false);
    };

    const moviePlaceholder = () => {
        return (
            <Card>
                <Grid container direction="column" alignItems="center">
                    <Grid item xs={12}>
                        <Typography variant="h5" style={{ marginTop: "20px" }}>It seems to be empty in here...</Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <Typography>Search and add movies to your nominations</Typography>
                    </Grid>
                    <Grid item xs={12} justify="center" style={{ padding: "0px 40px" }}>

                        <img alt="Empty theater seats" src={EmptyTheater} style={{
                            marginTop: '25px',
                            marginBottom: '25px',
                            width: '100%',
                        }} />
                    </Grid>

                </Grid>
            </Card>
        );
    };

    return (
        <div className={classes.root}>
            <div>
                {renderedList.length === 0 ? moviePlaceholder() : renderedList}
            </div>
            {
                (movies.length === 0) || (movies.length >= resultsAmount) ? '' :
                    <Button color="primary" variant="outlined" onClick={handleLoadMore}>More Results</Button>
            }
            <div>
                <AlertWarning content="Your nomination list is full!" handleOpen={openAlert} handleClose={handleClose} />
            </div>
        </div>
    );
}

export default MovieList;
