import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import NominationList from './components/NominationList';
import MovieList from './components/MovieList';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Header from './components/Header';

import omdb from './api/omdb';
import Toolbar from '@material-ui/core/Toolbar';

import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';
import Fab from '@material-ui/core/Fab';
import { makeStyles } from '@material-ui/core/styles';



const useStyles = makeStyles((theme) => ({
    root: {
        position: 'fixed',
        bottom: theme.spacing(2),
        right: theme.spacing(2),
    },
}));


const Nomination = (props) => {

    const [movies, setMovies] = useState([]);

    const [nominations, setNominations] = useState([]);

    const [searchError, setSearchError] = useState('');
    ////////////////////////////////////////////////////////////////////
    const [pageLoaded, setPageLoaded] = useState(1);

    const [resultsAmount, setResultsAmount] = useState(0);

    const [term, setTerm] = useState('');

    const onTermSubmit = async (term) => {
        // HTTPS request to OMDB API
        const response = await omdb.get(
            '',
            {
                params: {
                    s: term,
                }
            }
        );
        // console.log("Search response:");
        // console.log(response);

        // ////////////////////////////////////////////////////////////////////
        // console.log("Total result is " + response.data.totalResults);

        if (response.data.Response === "False") {
            setSearchError(response.data.Error);
        } else {
            setSearchError('');
            setTerm(term);
            setMovies(response.data.Search);
        }
    };

    //////////////////////////////////////////////////////////////////////
    const handleLoadMore = async () => {
        // HTTPS request to OMDB API
        console.log("Term is " + term)
        const response = await omdb.get(
            '',
            {
                params: {
                    s: term,
                    page: pageLoaded + 1,
                }
            }
        );
        console.log("Page " + (pageLoaded + 1));
        console.log(response.data.Search);

        ////////////////////////////////////////////////////////////////////
        console.log("Total result is " + response.data.totalResults);

        setPageLoaded(pageLoaded + 1);

        if (response.data.Response === "False") {
            setSearchError(response.data.Error);
        } else {
            setSearchError('');

            const newMovies = response.data.Search;
            newMovies.map((newMovie) => {
                setMovies(oldMovies => [...oldMovies, newMovie]);
            });


            console.log("There are now " + movies.length + " movies in total");
        }
    };

    const onNominate = (movie) => {
        setNominations(oldNominations => [...oldNominations, movie]);
    };

    const onRemoveNomination = (movie) => {
        const newList = nominations.filter((movieItem) => movieItem !== movie);
        setNominations(newList);

    };

    const ScrollTop = (props) => {
        const { children, window } = props;
        const classes = useStyles();
        // Note that you normally won't need to set the window ref as useScrollTrigger
        // will default to window.
        // This is only being set here because the demo is in an iframe.
        const trigger = useScrollTrigger({
            target: window ? window() : undefined,
            disableHysteresis: true,
            threshold: 100,
        });

        const handleClick = (event) => {
            const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');

            if (anchor) {
                anchor.scrollIntoView({ behavior: 'smooth', block: 'center' });
            }
        };

        return (
            <Zoom in={trigger}>
                <div onClick={handleClick} role="presentation" className={classes.root}>
                    {children}
                </div>
            </Zoom>
        );
    }

    return (
        <div style={{ backgroundColor: "#f9fafb" }}>
            <div>
                <Header />
                <Toolbar id="back-to-top-anchor" />
                <Container maxWidth="lg">
                    <Grid container spacing={5} justify="center">
                        <Grid item xs={12} sm={4}>
                            <NominationList nominations={nominations} onRemoveNomination={onRemoveNomination} />
                        </Grid>
                        <Grid item xs={12} sm={8}>
                            <div>
                                <SearchBar onTermSubmit={onTermSubmit} searchError={searchError} />
                                <MovieList movies={movies} onNominate={onNominate} nominations={nominations} searchError={searchError} handleLoadMore={handleLoadMore} />
                            </div>
                        </Grid>
                    </Grid>
                </Container>
            </div>
            <ScrollTop {...props}>
                <Fab color="secondary" size="small" aria-label="scroll back to top">
                    <KeyboardArrowUpIcon />
                </Fab>
            </ScrollTop>
        </div >
    );
};


export default Nomination;
