import React from 'react';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Slide from '@material-ui/core/Slide';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
    removeButton: {
        textTransform: "none",
        color: 'orangered',
        "&:hover": {
            color: "orangered",
            textDecoration: "underline",
            cursor: "pointer",
        }
    }
}));

const NominationItem = ({ nomination, onRemoveNomination }) => {
    const classes = useStyles();

    return (
        <Slide direction="up" in={true} mountOnEnter unmountOnExit>
            <Paper square variant="outlined" style={{ padding: "15px" }}>
                <div>{nomination.Title}</div>
                <Button variant="outlined" style={{ marginTop: "8px", borderColor: "#eb5155", textTransform: "none", color: "#eb5155" }} onClick={() => onRemoveNomination(nomination)}> Remove</Button>
            </Paper>
        </Slide>
    );
};

export default NominationItem;
