import React, { useState, useEffect } from 'react';
import Typography from '@material-ui/core/Typography';
import omdb from '../api/omdb';

const MovieDetails = ({ movieId }) => {

    const [actors, setActors] = useState('');
    const [awards, setAwards] = useState('');
    const [boxOffice, setBoxOffice] = useState('');
    const [country, setCountry] = useState('');
    const [director, setDirector] = useState('');
    const [genre, setGenre] = useState('');
    const [metascore, setMetascore] = useState('');
    const [runtime, setRuntime] = useState('');
    const [writer, setWriter] = useState('');
    const [releasedDate, setReleasedDate] = useState('');
    const [imdbRating, setImdbRating] = useState('');
    const [rottenTomatoesRating, setRottenTomatoesRating] = useState('');

    const onClick = async () => {
        const response = await omdb.get(
            '',
            {
                params: {
                    i: movieId
                }
            }
        );

        const results = response.data;

        setActors(results.Actors);
        setAwards(results.Awards);
        setBoxOffice(results.BoxOffice);
        setCountry(results.Country);
        setDirector(results.Director);
        setGenre(results.Genre);
        setMetascore(results.Metascore);
        setRuntime(results.Runtime);
        setWriter(results.Writer);
        setReleasedDate(results.Released);
        // setImdbRating(response.data.Ratings[0].Value);
        // setRottenTomatoesRating(response.data.Ratings[1].Value);

        console.log("Detail response:");
        console.log(response);
    };

    return (
        <div>
            <Typography>Actors: {actors}</Typography>
            <Typography>Awards: {awards}</Typography>
            <Typography>Box Office: {boxOffice}</Typography>
            <Typography>Country: {country}</Typography>
            <Typography>Director: {director}</Typography>
            <Typography>Genre: {genre}</Typography>
            <Typography>Metascore: {metascore}</Typography>
            <Typography>Runtime: {runtime}</Typography>
            <Typography>Writer: {writer}</Typography>
            <Typography>Date of Release: {releasedDate}</Typography>
            <Typography>IMDb Rating: {imdbRating}</Typography>
            <Typography>Rotten Tomatoes Rating: {rottenTomatoesRating}</Typography>
        </div>
    );
};

export default MovieDetails;
