import React from 'react';
import NominationItem from './NominationItem';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import oscarSearching from '../assets/oscar_search.png';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    paperPadding: {
        padding: '25px',
    },
    imgWidth: {
        width: '100%',
    },
    dividerMargin: {
        marginTop: '25px',
        marginBottom: '25px',
    },
});

const NominationList = ({ nominations, onRemoveNomination }) => {
    const classes = useStyles();

    const renderedList = nominations.map((nomination) => {
        return (
            <NominationItem key={nomination.imdbID} nomination={nomination} onRemoveNomination={onRemoveNomination} />
        );
    });

    if (nominations.length === 0) {
        return (
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item>
                            <Typography variant="h5">You have no nominations.</Typography>
                        </Grid>
                        <Grid item>
                            <Typography>Help Oscar the Penguin look for them!</Typography>
                        </Grid>
                        <Grid item>
                            <Divider className={classes.dividerMargin} variant="middle" />
                            <img className={classes.imgWidth} alt="Oscar the Penguin searching for award nominations" src={oscarSearching} />
                        </Grid>
                    </Grid>
                </Paper>
            </div>
        );
    }

    return (
        <div>
            <Paper variant="outlined" className={classes.paperPadding}>
                <Grid container>
                    <Grid item>
                        <Typography variant="h5">You have x nominations.</Typography>
                    </Grid>
                    <Grid item>
                        <Typography>Help Oscar the Penguin look for them!</Typography>
                    </Grid>
                </Grid>
            </Paper>
            {renderedList}
        </div>
    );
};

export default NominationList;
