import React from 'react';
import NominationItem from './NominationItem';

const NominationList = ({ nominations, onRemoveNomination }) => {
    const renderedList = nominations.map((nomination) => {
        return (
            <NominationItem key={nomination.imdbID} nomination={nomination} onRemoveNomination={onRemoveNomination} />
        );
    });

    if (nominations.length === 0) {
        return (
            <div>There are no nominations</div>
        );
    }

    return (
        <div style={{ backgroundColor: "green" }}>
            Nominations:
            {renderedList}
        </div>
    );
};

export default NominationList;
