import React from 'react';
import NominationItem from './NominationItem';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import oscarSearching from '../assets/oscar_search.png';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Pengu from '../assets/oscar_found.png';
import AlertWarning from './AlertWarning';


const useStyles = makeStyles((theme) => ({
    paperPadding: {
        padding: '25px',
    },
    imgOscarSearch: {
        marginTop: '25px',
        marginBottom: '15px',
        width: '100%',
    },
    secondaryHeading: {
        color: theme.palette.text.secondary,
        topMargin: '25px',
    },
}));


const NominationList = ({ nominations, onRemoveNomination }) => {
    const classes = useStyles();

    const [openAlert, setOpenAlert] = React.useState(false);
    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenAlert(false);
    };

    const renderedList = nominations.map((nomination) => {
        return (
            <NominationItem key={nomination.imdbID} nomination={nomination} onRemoveNomination={onRemoveNomination} />
        );
    });


    const handleAlert = () => {
        setOpenAlert(true);
    };

    if (nominations.length === 0) {
        return (
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">There are no nominations!</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={oscarSearching} />
                        </Grid>
                        <Grid item xs={12}>
                            <Typography align="center">Help Oscar the Penguin look for them</Typography>
                        </Grid>
                    </Grid>
                </Paper>
            </div>
        );
    }

    return (
        <div>
            <div>
                <Paper variant="outlined" className={classes.paperPadding}>
                    <Grid container>
                        <Grid item xs={12}>
                            <Typography align="center" variant="h5">You have xxx nominations!</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            {nominations.length === 5 ? <img className={classes.imgOscarSearch} alt="Oscar the Penguin searching for award nominations" src={Pengu} /> : ''}
                        </Grid>
                    </Grid>
                </Paper>
                {renderedList}
            </div>
            <div>
                <AlertWarning content="Garbage" handleOpen={nominations.length === 5 ? setOpenAlert(true) : setOpenAlert(false)} handleClose={handleClose} />
            </div>

        </div>
    );
};

export default NominationList;
