import './MovieItem.css';
import React from 'react';
import axios from 'axios';

const MovieItem = ({ movie, movieDetail }) => {
    const movieId = movie.imdbID;

    const onClick = async () => {
        const response = await axios.get(
            'http://www.omdbapi.com/?apikey=97e2a442&',
            {
                params: {
                    i: movieId
                }
            }
        );

        console.log("imbdID is " + movieId);
        console.log("Detail response:");
        console.log(response);
    };

    return (
        <div className="movie-item">
            <img onClick={() => { onClick() }} src={movie.Poster} />
            <div>
                <div>Title: {movie.Title}</div>
                {/* <div>Genre: {movieDetail.Genre}</div>
                <div>Rated: {movieDetail.Title}</div>
                <div>Run time: {movieDetail.Title}</div>
                <div>Title: {movieDetail.Title}</div> */}

            </div>
        </div>
    );
};

export default MovieItem;
